import React, { useState } from "react";
import "./styles/SettingsPanel.css";

export default function SettingsPanel({ onClose }) {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isMuted, setIsMuted] = useState(false);

  const handleThemeToggle = () => {
    setIsDarkMode((prev) => !prev);
    document.body.classList.toggle("dark-theme");
  };

  const handleMuteToggle = () => {
    setIsMuted((prev) => !prev);
  };

  return React.createElement(
    "div",
    { className: "settings-panel" },
    [
      React.createElement(
        "div",
        { key: "header", className: "settings-header" },
        [
          React.createElement(
            "button",
            { key: "back", className: "back-btn", onClick: onClose },
            "üîô Back"
          ),
          React.createElement("h2", { key: "title" }, "‚öôÔ∏è Settings"),
        ]
      ),
      React.createElement(
        "div",
        { key: "options", className: "settings-options" },
        [
          React.createElement(
            "div",
            { key: "theme", className: "setting-item" },
            [
              React.createElement("span", { key: "theme-label" }, "üåì Theme:"),
              React.createElement(
                "button",
                {
                  key: "theme-btn",
                  className: "toggle-btn",
                  onClick: handleThemeToggle,
                },
                isDarkMode ? "Switch to Light" : "Switch to Dark"
              ),
            ]
          ),
          React.createElement(
            "div",
            { key: "notifications", className: "setting-item" },
            [
              React.createElement(
                "span",
                { key: "notif-label" },
                "üîï Notifications:"
              ),
              React.createElement(
                "button",
                {
                  key: "notif-btn",
                  className: "toggle-btn",
                  onClick: handleMuteToggle,
                },
                isMuted ? "Unmute" : "Mute"
              ),
            ]
          ),
        ]
      ),
    ]
  );
}
