import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./TeacherDashboard.css";

export default function TeacherDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  const [students, setStudents] = useState([
    { id: 1, name: "Ali Khan", rollNo: "su72-bssem-033", attendance: null, marks: "" },
    { id: 2, name: "Sara Ahmed", rollNo: "su72-bssem-034", attendance: null, marks: "" },
    { id: 3, name: "Bilal Hussain", rollNo: "su72-bssem-035", attendance: null, marks: "" },
  ]);

  const [subjects, setSubjects] = useState([]);
  const [newSubject, setNewSubject] = useState("");

  // --- Logout ---
  const handleLogout = () => {
    navigate("/"); // Redirect to login page
  };

  // --- Attendance & Marks ---
  const toggleAttendance = (id) => {
    setStudents((prev) =>
      prev.map((s) =>
        s.id === id
          ? {
              ...s,
              attendance: s.attendance === "Present" ? "Absent" : "Present",
            }
          : s
      )
    );
  };

  const handleMarksChange = (id, marks) => {
    setStudents((prev) =>
      prev.map((s) => (s.id === id ? { ...s, marks } : s))
    );
  };

  const calculateAttendancePercentage = () => {
    const total = students.length;
    const present = students.filter((s) => s.attendance === "Present").length;
    return total === 0 ? 0 : ((present / total) * 100).toFixed(1);
  };

  const handleSave = () => {
    alert("‚úÖ Attendance and marks saved!");
    console.log(students);
  };

  // --- Subjects ---
  const handleAddSubject = (e) => {
    e.preventDefault();
    if (!newSubject.trim()) return alert("Please enter subject name");
    setSubjects([...subjects, { id: Date.now(), name: newSubject }]);
    setNewSubject("");
  };

  const handleDeleteSubject = (id) => {
    setSubjects(subjects.filter((s) => s.id !== id));
  };

  // --- Panels ---
  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div className="teacher-home">
            <h2 className="welcome-text">Welcome Teacher üë©‚Äçüè´</h2>
            <p className="sub-text">Use the sidebar to mark attendance or add subjects.</p>
          </div>
        );

      case "attendance":
        return (
          <div className="teacher-panel">
            <h2>‚úÖ Mark Attendance</h2>
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>Roll No</th>
                  <th>Name</th>
                  <th>Attendance</th>
                  <th>Marks</th>
                </tr>
              </thead>
              <tbody>
                {students.map((s) => (
                  <tr key={s.id}>
                    <td>{s.rollNo}</td>
                    <td>{s.name}</td>
                    <td>
                      <div className="attendance-toggle">
                        <span
                          className={`attendance-status ${
                            s.attendance === "Present"
                              ? "present-text"
                              : s.attendance === "Absent"
                              ? "absent-text"
                              : ""
                          }`}
                        >
                          {s.attendance ? s.attendance : "Not Marked"}
                        </span>
                        <button
                          className={`toggle-btn ${
                            s.attendance === "Present"
                              ? "present-btn"
                              : s.attendance === "Absent"
                              ? "absent-btn"
                              : ""
                          }`}
                          onClick={() => toggleAttendance(s.id)}
                        >
                          Mark
                        </button>
                      </div>
                    </td>
                    <td>
                      <input
                        type="number"
                        placeholder="Enter Marks"
                        value={s.marks}
                        onChange={(e) => handleMarksChange(s.id, e.target.value)}
                        className="marks-input"
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="summary">
              <p>
                üìä Attendance Percentage:{" "}
                <strong>{calculateAttendancePercentage()}%</strong>
              </p>
            </div>

            <button className="save-button" onClick={handleSave}>
              üíæ Save Attendance & Marks
            </button>
          </div>
        );

      case "subjects":
        return (
          <div className="teacher-panel">
            <h2>üìò Add Subjects</h2>
            <form onSubmit={handleAddSubject} className="subject-form">
              <input
                type="text"
                placeholder="Enter Subject Name"
                value={newSubject}
                onChange={(e) => setNewSubject(e.target.value)}
                className="subject-input"
              />
              <button type="submit" className="add-subject-btn">
                ‚ûï Add Subject
              </button>
            </form>

            <ul className="subject-list">
              {subjects.map((s) => (
                <li key={s.id} className="subject-item">
                  {s.name}
                  <button
                    onClick={() => handleDeleteSubject(s.id)}
                    className="delete-subject-btn"
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="teacher-dashboard">
      {/* Sidebar */}
      <div className="teacher-sidebar">
        <h3 className="teacher-logo">Teacher Panel</h3>

        <button
          className={`nav-btn ${activePanel === "home" ? "active" : ""}`}
          onClick={() => setActivePanel("home")}
        >
          üè† Home
        </button>

        <button
          className={`nav-btn ${activePanel === "attendance" ? "active" : ""}`}
          onClick={() => setActivePanel("attendance")}
        >
          ‚úÖ Mark Attendance
        </button>

        <button
          className={`nav-btn ${activePanel === "subjects" ? "active" : ""}`}
          onClick={() => setActivePanel("subjects")}
        >
          üìò Add Subject
        </button>

        {/* üö™ Logout Button */}
        <button className="logout-btn" onClick={handleLogout}>
          üö™ Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="teacher-main">{renderPanel()}</div>
    </div>
  );
}
