import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./TeacherDashboard.css";

export default function TeacherDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  const [students, setStudents] = useState([
    { id: 1, name: "Ali Khan", rollNo: "su72-bssem-033" },
    { id: 2, name: "Sara Ahmed", rollNo: "su72-bssem-034" },
    { id: 3, name: "Bilal Hussain", rollNo: "su72-bssem-035" },
  ]);

  // --- Examination Data ---
  const [examData, setExamData] = useState(
    students.map((s) => ({ ...s, subject: "", marks: "" }))
  );

  // --- Logout ---
  const handleLogout = () => {
    navigate("/"); // Redirect to login page
  };

  // --- Handle Subject and Marks Change ---
  const handleExamChange = (id, field, value) => {
    setExamData((prev) =>
      prev.map((s) => (s.id === id ? { ...s, [field]: value } : s))
    );
  };

  const handleSaveExamination = () => {
    alert("‚úÖ Examination data saved successfully!");
    console.log("Examination Data:", examData);
  };

  // --- Panels ---
  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div className="teacher-home">
            <h2 className="welcome-text">Welcome Teacher üë©‚Äçüè´</h2>
            <p className="sub-text">
              Use the sidebar to mark attendance or manage examinations.
            </p>
          </div>
        );

      case "attendance":
        return (
          <div className="teacher-panel">
            <h2>‚úÖ Mark Attendance</h2>
            <p>This section has been moved to the Examination panel with subjects.</p>
          </div>
        );

      case "examination":
        return (
          <div className="teacher-panel">
            <h2>üßæ Examination</h2>
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>Roll No</th>
                  <th>Name</th>
                  <th>Subject</th>
                  <th>Marks</th>
                </tr>
              </thead>
              <tbody>
                {examData.map((s) => (
                  <tr key={s.id}>
                    <td>{s.rollNo}</td>
                    <td>{s.name}</td>
                    <td>
                      <input
                        type="text"
                        placeholder="Enter Subject"
                        value={s.subject}
                        onChange={(e) =>
                          handleExamChange(s.id, "subject", e.target.value)
                        }
                        className="marks-input"
                      />
                    </td>
                    <td>
                      <input
                        type="number"
                        placeholder="Enter Marks"
                        value={s.marks}
                        onChange={(e) =>
                          handleExamChange(s.id, "marks", e.target.value)
                        }
                        className="marks-input"
                      />
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <button className="save-button" onClick={handleSaveExamination}>
              üíæ Save Examination Data
            </button>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="teacher-dashboard">
      {/* Sidebar */}
      <div className="teacher-sidebar">
        <h3 className="teacher-logo">Teacher Panel</h3>

        <button
          className={`nav-btn ${activePanel === "home" ? "active" : ""}`}
          onClick={() => setActivePanel("home")}
        >
          üè† Home
        </button>

        <button
          className={`nav-btn ${activePanel === "examination" ? "active" : ""}`}
          onClick={() => setActivePanel("examination")}
        >
          üßæ Examination
        </button>

        {/* üö™ Logout Button */}
        <button className="logout-btn" onClick={handleLogout}>
          üö™ Logout
        </button>
      </div>

      {/* Main Content */}
      <div className="teacher-main">{renderPanel()}</div>
    </div>
  );
}
