import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  // Mock data states
  const [teachers, setTeachers] = useState([
    { id: 1, name: "John Smith", subject: "Math" },
  ]);
  const [students, setStudents] = useState([
    { id: 1, name: "Ali Khan", grade: "10th" },
  ]);

  // Input states
  const [teacherName, setTeacherName] = useState("");
  const [teacherSubject, setTeacherSubject] = useState("");
  const [studentName, setStudentName] = useState("");
  const [studentGrade, setStudentGrade] = useState("");

  const handleLogout = () => {
    navigate("/");
  };

  // --- Add/Delete Teacher ---
  const addTeacher = (e) => {
    e.preventDefault();
    if (!teacherName || !teacherSubject) return;
    const newTeacher = {
      id: Date.now(),
      name: teacherName,
      subject: teacherSubject,
    };
    setTeachers([...teachers, newTeacher]);
    setTeacherName("");
    setTeacherSubject("");
  };

  const deleteTeacher = (id) => {
    setTeachers(teachers.filter((t) => t.id !== id));
  };

  // --- Add/Delete Student ---
  const addStudent = (e) => {
    e.preventDefault();
    if (!studentName || !studentGrade) return;
    const newStudent = {
      id: Date.now(),
      name: studentName,
      grade: studentGrade,
    };
    setStudents([...students, newStudent]);
    setStudentName("");
    setStudentGrade("");
  };

  const deleteStudent = (id) => {
    setStudents(students.filter((s) => s.id !== id));
  };

  // --- Render Panels ---
  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div style={styles.homePanel}>
            <h2 style={styles.flickerText}>Welcome Admin</h2>
          </div>
        );

      case "teacher":
        return (
          <div style={styles.panel}>
            <h2>üë©‚Äçüè´ Teacher Panel</h2>
            <form onSubmit={addTeacher} style={styles.form}>
              <input
                type="text"
                placeholder="Teacher Name"
                value={teacherName}
                onChange={(e) => setTeacherName(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Subject"
                value={teacherSubject}
                onChange={(e) => setTeacherSubject(e.target.value)}
                style={styles.input}
              />
              <button type="submit" style={styles.addButton}>
                ‚ûï Add Teacher
              </button>
            </form>

            <ul style={styles.list}>
              {teachers.map((t) => (
                <li key={t.id} style={styles.listItem}>
                  {t.name} ‚Äì <em>{t.subject}</em>
                  <button
                    onClick={() => deleteTeacher(t.id)}
                    style={styles.deleteButton}
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          </div>
        );

      case "student":
        return (
          <div style={styles.panel}>
            <h2>üë®‚Äçüéì Student Panel</h2>
            <form onSubmit={addStudent} style={styles.form}>
              <input
                type="text"
                placeholder="Student Name"
                value={studentName}
                onChange={(e) => setStudentName(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Grade"
                value={studentGrade}
                onChange={(e) => setStudentGrade(e.target.value)}
                style={styles.input}
              />
              <button type="submit" style={styles.addButton}>
                ‚ûï Add Student
              </button>
            </form>

            <ul style={styles.list}>
              {students.map((s) => (
                <li key={s.id} style={styles.listItem}>
                  {s.name} ‚Äì <em>{s.grade}</em>
                  <button
                    onClick={() => deleteStudent(s.id)}
                    style={styles.deleteButton}
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h3 style={styles.logo}>Admin Panel</h3>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "home" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("home")}
        >
          üè† Home
        </button>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "teacher" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("teacher")}
        >
          üë©‚Äçüè´ Teacher Panel
        </button>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "student" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("student")}
        >
          üë®‚Äçüéì Student Panel
        </button>
        <button onClick={handleLogout} style={styles.logoutButton}>
          üö™ Logout
        </button>
      </div>

      {/* Main Content */}
      <div style={styles.mainContent}>{renderPanel()}</div>
    </div>
  );
}

// ---------- Styles ----------
const styles = {
  container: {
    display: "flex",
    height: "100vh",
    backgroundColor: "#f4f6f8",
  },
  sidebar: {
    width: "250px",
    backgroundColor: "#2c3e50",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px",
  },
  logo: {
    textAlign: "center",
    marginBottom: "30px",
    fontSize: "20px",
  },
  navButton: {
    backgroundColor: "#2c3e50",
    color: "#fff",
    border: "none",
    textAlign: "left",
    padding: "12px 16px",
    borderRadius: "6px",
    margin: "6px 0",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background-color 0.2s",
  },
  logoutButton: {
    marginTop: "auto",
    backgroundColor: "#e74c3c",
    border: "none",
    color: "#fff",
    padding: "10px",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
  },
  mainContent: {
    flex: 1,
    padding: "40px",
    textAlign: "center",
    overflowY: "auto",
  },
  homePanel: {
    marginTop: "100px",
  },
  flickerText: {
    fontSize: "40px",
    color: "#2980b9",
    animation: "flicker 1s infinite",
  },
  panel: {
    marginTop: "40px",
  },
  form: {
    marginBottom: "20px",
  },
  input: {
    margin: "6px",
    padding: "8px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    width: "180px",
  },
  addButton: {
    padding: "8px 14px",
    backgroundColor: "#27ae60",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  list: {
    listStyleType: "none",
    padding: 0,
  },
  listItem: {
    backgroundColor: "#fff",
    margin: "8px auto",
    padding: "10px",
    borderRadius: "6px",
    width: "300px",
    boxShadow: "0 1px 4px rgba(0,0,0,0.1)",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  deleteButton: {
    backgroundColor: "#e74c3c",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    padding: "4px 8px",
    cursor: "pointer",
  },
};

// Add flicker animation
const styleSheet = document.createElement("style");
styleSheet.innerText = `
@keyframes flicker {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}
`;
document.head.appendChild(styleSheet);
