import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  const handleLogout = () => {
    navigate("/");
  };

  // Panels for Home, Teacher, and Student
  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div style={styles.homePanel}>
            <h2 style={styles.flickerText}>Welcome Admin</h2>
          </div>
        );
      case "teacher":
        return (
          <div style={styles.panel}>
            <h2>Teacher Panel</h2>
            <p>Here you can manage teachers ‚Äî add, edit, or remove teachers.</p>
          </div>
        );
      case "student":
        return (
          <div style={styles.panel}>
            <h2>Student Panel</h2>
            <p>Here you can manage students ‚Äî view records or add new ones.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h3 style={styles.logo}>Admin Panel</h3>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "home" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("home")}
        >
          üè† Home
        </button>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "teacher" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("teacher")}
        >
          üë©‚Äçüè´ Teacher Panel
        </button>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "student" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("student")}
        >
          üë®‚Äçüéì Student Panel
        </button>

        <button onClick={handleLogout} style={styles.logoutButton}>
          üö™ Logout
        </button>
      </div>

      {/* Main Content */}
      <div style={styles.mainContent}>{renderPanel()}</div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    height: "100vh",
    backgroundColor: "#f4f6f8",
  },
  sidebar: {
    width: "250px",
    backgroundColor: "#2c3e50",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px",
  },
  logo: {
    textAlign: "center",
    marginBottom: "30px",
    fontSize: "20px",
  },
  navButton: {
    backgroundColor: "#2c3e50",
    color: "#fff",
    border: "none",
    textAlign: "left",
    padding: "12px 16px",
    borderRadius: "6px",
    margin: "6px 0",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background-color 0.2s",
  },
  logoutButton: {
    marginTop: "auto",
    backgroundColor: "#e74c3c",
    border: "none",
    color: "#fff",
    padding: "10px",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
  },
  mainContent: {
    flex: 1,
    padding: "40px",
    textAlign: "center",
  },
  homePanel: {
    marginTop: "100px",
  },
  flickerText: {
    fontSize: "40px",
    color: "#2980b9",
    animation: "flicker 1s infinite",
  },
  panel: {
    marginTop: "100px",
    fontSize: "18px",
  },
};

// Add flicker animation using CSS keyframes
const styleSheet = document.createElement("style");
styleSheet.innerText = `
@keyframes flicker {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.3; }
}
`;
document.head.appendChild(styleSheet);
