import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  // Mock data
  const [teachers, setTeachers] = useState([]);
  const [students, setStudents] = useState([]);

  // Teacher form states
  const [teacherName, setTeacherName] = useState("");
  const [teacherAge, setTeacherAge] = useState("");
  const [teacherQualification, setTeacherQualification] = useState("");
  const [teacherContact, setTeacherContact] = useState("");
  const [teacherAddress, setTeacherAddress] = useState("");

  // Student form states
  const [studentName, setStudentName] = useState("");
  const [studentFatherName, setStudentFatherName] = useState("");
  const [studentAge, setStudentAge] = useState("");
  const [studentDob, setStudentDob] = useState("");
  const [studentGender, setStudentGender] = useState("");
  const [studentContact, setStudentContact] = useState("");
  const [studentAddress, setStudentAddress] = useState("");

  // Logout
  const handleLogout = () => {
    navigate("/");
  };

  // Generate unique roll number
  const generateRollNo = () => {
    const lastStudent = students[students.length - 1];
    let nextIdNumber = 33;
    if (lastStudent) {
      const lastId = lastStudent.rollNo;
      const match = lastId.match(/(\d+)$/);
      if (match) nextIdNumber = parseInt(match[1]) + 1;
    }
    return `su72-bssem-${String(nextIdNumber).padStart(3, "0")}`;
  };

  // Add Teacher
  const addTeacher = (e) => {
    e.preventDefault();
    if (
      !teacherName ||
      !teacherAge ||
      !teacherQualification ||
      !teacherContact ||
      !teacherAddress
    )
      return alert("Please fill all teacher fields");

    const newTeacher = {
      id: Date.now(),
      name: teacherName,
      age: teacherAge,
      qualification: teacherQualification,
      contact: teacherContact,
      address: teacherAddress,
    };

    setTeachers([...teachers, newTeacher]);
    setTeacherName("");
    setTeacherAge("");
    setTeacherQualification("");
    setTeacherContact("");
    setTeacherAddress("");
  };

  // Delete Teacher
  const deleteTeacher = (id) => {
    setTeachers(teachers.filter((t) => t.id !== id));
  };

  // Add Student
  const addStudent = (e) => {
    e.preventDefault();
    if (
      !studentName ||
      !studentFatherName ||
      !studentAge ||
      !studentDob ||
      !studentGender ||
      !studentContact ||
      !studentAddress
    )
      return alert("Please fill all student fields");

    const newStudent = {
      id: Date.now(),
      rollNo: generateRollNo(),
      name: studentName,
      fatherName: studentFatherName,
      age: studentAge,
      dob: studentDob,
      gender: studentGender,
      contact: studentContact,
      address: studentAddress,
    };

    setStudents([...students, newStudent]);
    // Reset form
    setStudentName("");
    setStudentFatherName("");
    setStudentAge("");
    setStudentDob("");
    setStudentGender("");
    setStudentContact("");
    setStudentAddress("");
  };

  // Delete Student
  const deleteStudent = (id) => {
    setStudents(students.filter((s) => s.id !== id));
  };

  // --- Panels ---
  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div style={styles.homePanel}>
            <h2 style={styles.flickerText}>Welcome Admin</h2>
          </div>
        );

      case "teacher":
        return (
          <div style={styles.panel}>
            <h2>üë©‚Äçüè´ Teacher Panel</h2>
            <form onSubmit={addTeacher} style={styles.form}>
              <input
                type="text"
                placeholder="Name"
                value={teacherName}
                onChange={(e) => setTeacherName(e.target.value)}
                style={styles.input}
              />
              <input
                type="number"
                placeholder="Age"
                value={teacherAge}
                onChange={(e) => setTeacherAge(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Qualification"
                value={teacherQualification}
                onChange={(e) => setTeacherQualification(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Contact No"
                value={teacherContact}
                onChange={(e) => setTeacherContact(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Home Address"
                value={teacherAddress}
                onChange={(e) => setTeacherAddress(e.target.value)}
                style={styles.input}
              />
              <div style={styles.buttonContainer}>
                <button type="submit" style={styles.addButton}>
                  ‚ûï Add Teacher
                </button>
              </div>

            </form>

            <ul style={styles.list}>
              {teachers.map((t) => (
                <li key={t.id} style={styles.listItem}>
                  <div style={{ textAlign: "left" }}>
                    <strong>{t.name}</strong> ({t.age} yrs) <br />
                    Qualification: {t.qualification} <br />
                    Contact: {t.contact} <br />
                    Address: {t.address}
                  </div>
                  <button
                    onClick={() => deleteTeacher(t.id)}
                    style={styles.deleteButton}
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          </div>
        );

      case "student":
        return (
          <div style={styles.panel}>
            <h2>üë®‚Äçüéì Student Panel</h2>
            <form onSubmit={addStudent} style={styles.form}>
              <input
                type="text"
                placeholder="Name"
                value={studentName}
                onChange={(e) => setStudentName(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Father Name"
                value={studentFatherName}
                onChange={(e) => setStudentFatherName(e.target.value)}
                style={styles.input}
              />
              <input
                type="number"
                placeholder="Age"
                value={studentAge}
                onChange={(e) => setStudentAge(e.target.value)}
                style={styles.input}
              />
              <input
                type="date"
                value={studentDob}
                onChange={(e) => setStudentDob(e.target.value)}
                style={styles.input}
              />
              <select
                value={studentGender}
                onChange={(e) => setStudentGender(e.target.value)}
                style={styles.input}
              >
                <option value="">Select Gender</option>
                <option value="Male">Male</option>
                <option value="Female">Female</option>
              </select>
              <input
                type="text"
                placeholder="Contact No"
                value={studentContact}
                onChange={(e) => setStudentContact(e.target.value)}
                style={styles.input}
              />
              <input
                type="text"
                placeholder="Home Address"
                value={studentAddress}
                onChange={(e) => setStudentAddress(e.target.value)}
                style={styles.input}
              />
              <div style={styles.buttonContainer}>
                <button type="submit" style={styles.addButton}>
                  ‚ûï Add Student
                </button>
              </div>

            </form>

            <ul style={styles.list}>
              {students.map((s) => (
                <li key={s.id} style={styles.listItem}>
                  <div style={{ textAlign: "left" }}>
                    <strong>{s.name}</strong> (Roll No: {s.rollNo}) <br />
                    Father: {s.fatherName} | Gender: {s.gender} <br />
                    Age: {s.age} | DOB: {s.dob} <br />
                    Contact: {s.contact} <br />
                    Address: {s.address}
                  </div>
                  <button
                    onClick={() => deleteStudent(s.id)}
                    style={styles.deleteButton}
                  >
                    ‚ùå
                  </button>
                </li>
              ))}
            </ul>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <h3 style={styles.logo}>Admin Panel</h3>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "home" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("home")}
        >
          üè† Home
        </button>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "teacher" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("teacher")}
        >
          üë©‚Äçüè´ Teacher Panel
        </button>
        <button
          style={{
            ...styles.navButton,
            backgroundColor: activePanel === "student" ? "#34495e" : "#2c3e50",
          }}
          onClick={() => setActivePanel("student")}
        >
          üë®‚Äçüéì Student Panel
        </button>
        <button onClick={handleLogout} style={styles.logoutButton}>
          üö™ Logout
        </button>
      </div>

      {/* Main Content */}
      <div style={styles.mainContent}>{renderPanel()}</div>
    </div>
  );
}

// ---------- Styles ----------
