  container: { display: "flex", height: "100vh", backgroundColor: "#f4f6f8" },
  sidebar: {
    width: "250px",
    backgroundColor: "#2c3e50",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    padding: "20px",
  },
  logo: { textAlign: "center", marginBottom: "30px", fontSize: "20px" },
  navButton: {
    backgroundColor: "#2c3e50",
    color: "#fff",
    border: "none",
    textAlign: "left",
    padding: "12px 16px",
    borderRadius: "6px",
    margin: "6px 0",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background-color 0.2s",
  },
  logoutButton: {
    marginTop: "auto",
    backgroundColor: "#e74c3c",
    border: "none",
    color: "#fff",
    padding: "10px",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
  },
  mainContent: { flex: 1, padding: "40px", textAlign: "center", overflowY: "auto" },
  homePanel: { marginTop: "100px" },
  flickerText: { fontSize: "40px", color: "#2980b9", animation: "flicker 1s infinite" },
  panel: { marginTop: "40px" },
  form: { marginBottom: "20px" },
  input: {
    margin: "6px",
    padding: "8px",
    borderRadius: "4px",
    border: "1px solid #ccc",
    width: "180px",
  },
  addButton: {
    padding: "8px 14px",
    backgroundColor: "#27ae60",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  },
  list: { listStyleType: "none", padding: 0 },
  listItem: {
