import React, { useState } from "react";

export default function CoursesPanel({ courses, teachers, addCourse, deleteCourse }) {
  const [courseName, setCourseName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [credits, setCredits] = useState(1);
  const [department, setDepartment] = useState("");
  const [assignedTeacherId, setAssignedTeacherId] = useState("");
  const [assignedClass, setAssignedClass] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      !courseName ||
      !courseCode ||
      !department ||
      !assignedTeacherId ||
      !assignedClass
    ) {
      return alert("Please provide all fields.");
    }

    addCourse({
      name: courseName,
      code: courseCode,
      credits,
      assignedTeacherId,
      assignedClass,
      department,
    });

    setCourseName("");
    setCourseCode("");
    setCredits(1);
    setDepartment("");
    setAssignedTeacherId("");
    setAssignedClass("");
  };

  return (
    <div className="panel">
      <h2>üìö Courses & Curriculum</h2>

      <form onSubmit={handleSubmit}>
        <div className="panel-form-row">
          <div className="field">
            <input
              className="input"
              type="text"
              placeholder="Course name"
              value={courseName}
              onChange={(e) => setCourseName(e.target.value)}
            />
          </div>

          <div className="field xsmall">
            <input
              className="input"
              type="number"
              placeholder="Credits"
              value={credits}
              min="0"
              onChange={(e) => setCredits(Number(e.target.value))}
            />
          </div>

          <div className="field narrow">
            <input
              className="input"
              type="text"
              placeholder="Code (e.g., BSSE-101)"
              value={courseCode}
              onChange={(e) => setCourseCode(e.target.value)}
            />
          </div>

          <div className="field">
            <input
              className="input"
              type="text"
              placeholder="Department"
              value={department}
              onChange={(e) => setDepartment(e.target.value)}
            />
          </div>

          <div className="field">
            <select
              className="input"
              value={assignedTeacherId}
              onChange={(e) => setAssignedTeacherId(e.target.value)}
            >
              <option value="">Assign Teacher</option>
              {teachers.map((t) => (
                <option key={t.id} value={t.id}>
                  {t.name}
                </option>
              ))}
            </select>
          </div>

          <div className="field narrow">
            <input
              className="input"
              type="text"
              placeholder="Class (e.g., BSSE-1)"
              value={assignedClass}
              onChange={(e) => setAssignedClass(e.target.value)}
            />
          </div>

          <div style={{ display: "flex", alignItems: "center" }}>
            <button className="create-button" type="submit">
              + Create
            </button>
          </div>
        </div>
      </form>

      <ul className="list">
        {courses.length === 0 ? (
          <li style={{ color: "var(--muted)" }}>No courses yet.</li>
        ) : (
          courses.map((c) => (
            <li key={c.id} className="list-item">
              <div>
                <strong>{c.name}</strong> ({c.code}) ‚Äî {c.department} <br />
                Teacher: {c.assignedTeacherName} | Class: {c.assignedClass}
              </div>
              <button className="delete-button" onClick={() => deleteCourse(c.id)}>
                ‚ùå
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
