import React, { useState, useEffect } from "react";
import axios from "axios";

export default function TeacherDashboard() {
  const [students, setStudents] = useState([]);
  const [marks, setMarks] = useState({});

  const fetchStudents = async () => {
    const res = await axios.get("http://localhost:5000/users?role=student");
    setStudents(res.data);
  };

  const markAttendance = async (id, present) => {
    await axios.post("http://localhost:5000/attendance", { studentId: id, present });
    alert("Attendance updated");
  };

  const handleMarkChange = (id, value) => {
    setMarks({ ...marks, [id]: value });
  };

  const submitMarks = async (id) => {
    await axios.post("http://localhost:5000/marks", { studentId: id, mark: marks[id] });
    alert("Marks added!");
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Teacher Dashboard</h2>
      <table className="border w-full">
        <thead>
          <tr className="bg-gray-200">
            <th>Name</th>
            <th>Attendance</th>
            <th>Marks</th>
          </tr>
        </thead>
        <tbody>
          {students.map((s) => (
            <tr key={s.id}>
              <td>{s.username}</td>
              <td>
                <button onClick={() => markAttendance(s.id, true)} className="bg-green-500 text-white px-2">
                  Present
                </button>
                <button onClick={() => markAttendance(s.id, false)} className="bg-red-500 text-white px-2 ml-2">
                  Absent
                </button>
              </td>
              <td>
                <input
                  type="number"
                  value={marks[s.id] || ""}
                  onChange={(e) => handleMarkChange(s.id, e.target.value)}
                  className="border p-1 w-16"
                />
                <button
                  onClick={() => submitMarks(s.id)}
                  className="bg-blue-500 text-white px-2 ml-2"
                >
                  Save
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
