import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./components/Login";
import AdminDashboard from "./components/AdminDashboard";
import TeacherDashboard from "./components/TeacherDashboard";
import ExaminationPanel from "./components/ExaminationPanel";
import ExaminationClassPage from "./components/ExaminationClassPage";
import { SystemSettingsProvider } from "./context/SystemSettingsContext";
import SettingsPanel from "./components/SettingsPanel"; // ✅ Adjust path if needed

export default function App() {
  return (
    <SystemSettingsProvider>
      <Router>
        {/* Global Settings Panel - optional, can be hidden via a button */}
        <SettingsPanel onClose={() => console.log("Settings closed")} />

        {/* Application Routes */}
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/admin" element={<AdminDashboard />} />
          <Route path="/teacher" element={<TeacherDashboard />} />
          <Route path="/examination" element={<ExaminationPanel />} />
          <Route path="/examination/class" element={<ExaminationClassPage />} />
        </Routes>
      </Router>
    </SystemSettingsProvider>
  );
}
