import React, { useState } from "react";
import "./TeacherDashboard.css";

export default function TeacherDashboard() {
  // Mock student list
  const [students, setStudents] = useState([
    { id: 1, name: "Ali Khan", rollNo: "su72-bssem-033", attendance: "", marks: "" },
    { id: 2, name: "Sara Ahmed", rollNo: "su72-bssem-034", attendance: "", marks: "" },
    { id: 3, name: "Bilal Hussain", rollNo: "su72-bssem-035", attendance: "", marks: "" },
  ]);

  // Handle attendance
  const handleAttendance = (id, status) => {
    const updated = students.map((s) =>
      s.id === id ? { ...s, attendance: status } : s
    );
    setStudents(updated);
  };

  // Handle marks input
  const handleMarksChange = (id, marks) => {
    const updated = students.map((s) =>
      s.id === id ? { ...s, marks } : s
    );
    setStudents(updated);
  };

  // Save marks (mock)
  const handleSave = () => {
    alert("‚úÖ Marks & attendance saved successfully!");
    console.log(students);
  };

  return (
    <div className="teacher-container">
      <h2 className="teacher-heading">üë©‚Äçüè´ Teacher Dashboard</h2>

      <table className="teacher-table">
        <thead>
          <tr>
            <th>Roll No</th>
            <th>Name</th>
            <th>Attendance</th>
            <th>Marks</th>
          </tr>
        </thead>
        <tbody>
          {students.map((s) => (
            <tr key={s.id}>
              <td>{s.rollNo}</td>
              <td>{s.name}</td>
              <td>
                <select
                  value={s.attendance}
                  onChange={(e) => handleAttendance(s.id, e.target.value)}
                  className="attendance-select"
                >
                  <option value="">Select</option>
                  <option value="Present">Present</option>
                  <option value="Absent">Absent</option>
                </select>
              </td>
              <td>
                <input
                  type="number"
                  placeholder="Enter Marks"
                  value={s.marks}
                  onChange={(e) => handleMarksChange(s.id, e.target.value)}
                  className="marks-input"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="save-container">
        <button className="save-button" onClick={handleSave}>
          üíæ Save Attendance & Marks
        </button>
      </div>
    </div>
  );
}
