import React, { useState } from "react";
import "./TeacherDashboard.css";

export default function TeacherDashboard() {
  // Mock student list
  const [students, setStudents] = useState([
    { id: 1, name: "Ali Khan", rollNo: "su72-bssem-033", attendance: null, marks: "" },
    { id: 2, name: "Sara Ahmed", rollNo: "su72-bssem-034", attendance: null, marks: "" },
    { id: 3, name: "Bilal Hussain", rollNo: "su72-bssem-035", attendance: null, marks: "" },
    { id: 4, name: "Hina Malik", rollNo: "su72-bssem-036", attendance: null, marks: "" },
  ]);

  // Handle attendance
  const handleAttendance = (id, status) => {
    const updated = students.map((s) =>
      s.id === id ? { ...s, attendance: status } : s
    );
    setStudents(updated);
  };

  // Handle marks input
  const handleMarksChange = (id, marks) => {
    const updated = students.map((s) =>
      s.id === id ? { ...s, marks } : s
    );
    setStudents(updated);
  };

  // Calculate attendance percentage
  const calculateAttendancePercentage = () => {
    const total = students.length;
    const presentCount = students.filter((s) => s.attendance === "Present").length;
    return total === 0 ? 0 : ((presentCount / total) * 100).toFixed(1);
  };

  const handleSave = () => {
    alert("‚úÖ Attendance and marks saved successfully!");
    console.log(students);
  };

  return (
    <div className="teacher-container">
      <h2 className="teacher-heading">üë©‚Äçüè´ Teacher Dashboard</h2>

      <table className="teacher-table">
        <thead>
          <tr>
            <th>Roll No</th>
            <th>Name</th>
            <th>Attendance</th>
            <th>Marks</th>
          </tr>
        </thead>
        <tbody>
          {students.map((s) => (
            <tr key={s.id}>
              <td>{s.rollNo}</td>
              <td>{s.name}</td>
              <td>
                <div className="attendance-buttons">
                  <button
                    className={`attendance-btn ${
                      s.attendance === "Present" ? "present" : ""
                    }`}
                    onClick={() => handleAttendance(s.id, "Present")}
                  >
                    Present
                  </button>
                  <button
                    className={`attendance-btn ${
                      s.attendance === "Absent" ? "absent" : ""
                    }`}
                    onClick={() => handleAttendance(s.id, "Absent")}
                  >
                    Absent
                  </button>
                </div>
              </td>
              <td>
                <input
                  type="number"
                  placeholder="Enter Marks"
                  value={s.marks}
                  onChange={(e) => handleMarksChange(s.id, e.target.value)}
                  className="marks-input"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="summary">
        <p>
          üìä Attendance Percentage:{" "}
          <strong>{calculateAttendancePercentage()}%</strong>
        </p>
      </div>

      <div className="save-container">
        <button className="save-button" onClick={handleSave}>
          üíæ Save Attendance & Marks
        </button>
      </div>
    </div>
  );
}
