import { useState } from "react";
import { initialTeachers, initialStudents } from "./mockData";

export default function useAdminState() {
  const [teachers, setTeachers] = useState(initialTeachers);
  const [students, setStudents] = useState(initialStudents);

  const generateRollNo = () => {
    const lastStudent = students[students.length - 1];
    let nextIdNumber = 33;
    if (lastStudent) {
      const lastId = lastStudent.rollNo;
      const match = lastId && lastId.match(/(\d+)$/);
      if (match) nextIdNumber = parseInt(match[1]) + 1;
    }
    return `su72-bssem-${String(nextIdNumber).padStart(3, "0")}`;
  };

  const addTeacher = (teacher) => {
    const newTeacher = {
      id: Date.now(),
      ...teacher,
    };
    setTeachers((prev) => [...prev, newTeacher]);
  };

  const deleteTeacher = (id) => {
    setTeachers((prev) => prev.filter((t) => t.id !== id));
  };

  const addStudent = (student) => {
    const s = {
      id: Date.now(),
      rollNo: generateRollNo(),
      ...student,
    };
    setStudents((prev) => [...prev, s]);
  };

  const deleteStudent = (id) => {
    setStudents((prev) => prev.filter((s) => s.id !== id));
  };

  return {
    teachers,
    students,
    addTeacher,
    deleteTeacher,
    addStudent,
    deleteStudent,
    generateRollNo,
  };
}
