import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { studentData } from "../data/studentData";
import "./styles/ExaminationPanel.css";

export default function ExaminationClassPage() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const [students, setStudents] = useState([]);
  const [marks, setMarks] = useState({});

  const { classGrade, section, stream } = state || {};

  useEffect(() => {
    // Redirect if no data passed
    if (!classGrade || !section) {
      navigate("/teacher/examination");
      return;
    }

    let key = `${classGrade}-${section}`;
    if ((classGrade === 9 || classGrade === 10) && stream) {
      key += `-${stream}`;
    }

    setStudents(studentData[key] || []);
  }, [classGrade, section, stream, navigate]);

  const handleMarkChange = (student, value) => {
    setMarks((prev) => ({ ...prev, [student.rollNo]: value }));
  };

  const handleSubmitMarks = () => {
    console.log("Submitted Marks:", marks);
    alert("‚úÖ Marks uploaded successfully!");
  };

  // ‚úÖ This function will return to the ExaminationPanel inside TeacherDashboard
  const handleClose = () => {
    navigate("/teacher/examination");
  };

  if (!students.length) {
    return <p>No students found for this class.</p>;
  }

  return (
    <div className="exam-class-page">
      {/* ‚ùå Close button (top-right corner) */}
      <button className="close-btn" onClick={handleClose}>
        ‚ùå
      </button>

      <h2>
        üìò Class {classGrade} - Section {section}{" "}
        {(classGrade === 9 || classGrade === 10) && stream && `(${stream})`}
      </h2>

      <table className="exam-table">
        <thead>
          <tr>
            <th>Roll No</th>
            <th>Student Name</th>
            <th>Marks</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
            <tr key={index}>
              <td>{student.rollNo}</td>
              <td>{student.name}</td>
              <td>
                <input
                  type="number"
                  placeholder="Enter Marks"
                  value={marks[student.rollNo] || ""}
                  onChange={(e) => handleMarkChange(student, e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="exam-actions">
        <button onClick={handleSubmitMarks} className="submit-marks-btn">
          üíæ Save & Upload Marks
        </button>
      </div>
    </div>
  );
}
