import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  // Persisted data (localStorage)
  const [teachers, setTeachers] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("teachers")) || [];
    } catch {
      return [];
    }
  });
  const [students, setStudents] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("students")) || [];
    } catch {
      return [];
    }
  });

  // Search states
  const [teacherQuery, setTeacherQuery] = useState("");
  const [studentQuery, setStudentQuery] = useState("");

  // Teacher form states
  const [teacherName, setTeacherName] = useState("");
  const [teacherAge, setTeacherAge] = useState("");
  const [teacherQualification, setTeacherQualification] = useState("");
  const [teacherCnic, setTeacherCnic] = useState("");
  const [teacherContact, setTeacherContact] = useState("");
  const [teacherAddress, setTeacherAddress] = useState("");

  // Student form states
  const [studentName, setStudentName] = useState("");
  const [studentFatherName, setStudentFatherName] = useState("");
  const [studentCnic, setStudentCnic] = useState("");
  const [studentAge, setStudentAge] = useState("");
  const [studentDob, setStudentDob] = useState("");
  const [studentGender, setStudentGender] = useState("");
  const [studentContact, setStudentContact] = useState("");
  const [studentAddress, setStudentAddress] = useState("");

  useEffect(() => {
    localStorage.setItem("teachers", JSON.stringify(teachers));
  }, [teachers]);

  useEffect(() => {
    localStorage.setItem("students", JSON.stringify(students));
  }, [students]);

  // Logout
  const handleLogout = () => {
    navigate("/");
  };

  // Generate unique roll number
  const generateRollNo = () => {
    const lastStudent = students[students.length - 1];
    let nextIdNumber = 33;
    if (lastStudent) {
      const lastId = lastStudent.rollNo;
      const match = lastId && lastId.match(/(\d+)$/);
      if (match) nextIdNumber = parseInt(match[1], 10) + 1;
    }
    return `SU72-BSSEM-${String(nextIdNumber).padStart(3, "0")}`;
  };

  // Add Teacher
  const addTeacher = (e) => {
    e.preventDefault();
    if (
      !teacherName ||
      !teacherAge ||
      !teacherQualification ||
      !teacherCnic ||
      !teacherContact ||
      !teacherAddress
    )
      return alert("Please fill all teacher fields");

    const newTeacher = {
      id: Date.now(),
      name: teacherName.trim(),
      age: teacherAge,
      qualification: teacherQualification.trim(),
      cnic: teacherCnic.trim(),
      contact: teacherContact.trim(),
      address: teacherAddress.trim(),
    };

    setTeachers((prev) => [...prev, newTeacher]);
    setTeacherName("");
    setTeacherAge("");
    setTeacherQualification("");
    setTeacherCnic("");
    setTeacherContact("");
    setTeacherAddress("");
    setActivePanel("teacher");
  };

  // Delete Teacher
  const deleteTeacher = (id) => {
    if (!window.confirm("Delete this teacher?")) return;
    setTeachers((prev) => prev.filter((t) => t.id !== id));
  };

  // Add Student
  const addStudent = (e) => {
    e.preventDefault();
    if (
      !studentName ||
      !studentFatherName ||
      !studentCnic ||
      !studentAge ||
      !studentDob ||
      !studentGender ||
      !studentContact ||
      !studentAddress
    )
      return alert("Please fill all student fields");

    const newStudent = {
      id: Date.now(),
      rollNo: generateRollNo(),
      name: studentName.trim(),
      fatherName: studentFatherName.trim(),
      cnic: studentCnic.trim(),
      age: studentAge,
      dob: studentDob,
      gender: studentGender,
      contact: studentContact.trim(),
      address: studentAddress.trim(),
    };

    setStudents((prev) => [...prev, newStudent]);
    setStudentName("");
    setStudentFatherName("");
    setStudentCnic("");
    setStudentAge("");
    setStudentDob("");
    setStudentGender("");
    setStudentContact("");
    setStudentAddress("");
    setActivePanel("student");
  };

  // Delete Student
  const deleteStudent = (id) => {
    if (!window.confirm("Delete this student?")) return;
    setStudents((prev) => prev.filter((s) => s.id !== id));
  };

  // Simple avatar initials
  const Avatar = ({ name }) => {
    const initials = (name || "")
      .split(" ")
      .map((n) => n[0])
      .slice(0, 2)
      .join("")
      .toUpperCase();
    return <div className="avatar">{initials || "?"}</div>;
  };

  // Filters
  const filteredTeachers = teachers.filter((t) =>
    `${t.name} ${t.qualification} ${t.cnic}`.toLowerCase().includes(teacherQuery.toLowerCase())
  );

  const filteredStudents = students.filter((s) =>
    `${s.name} ${s.fatherName} ${s.rollNo} ${s.cnic}`.toLowerCase().includes(studentQuery.toLowerCase())
  );

  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div className="home-panel card">
            <h2 className="flicker-text">Welcome Admin</h2>
            <p className="muted">
              Teachers: <strong>{teachers.length}</strong> â€¢ Students: <strong>{students.length}</strong>
            </p>
            <div className="quick-actions">
              <button className="primary" onClick={() => setActivePanel("teacher")}>
                â• Add Teacher
              </button>
              <button className="primary outline" onClick={() => setActivePanel("student")}>
                â• Add Student
              </button>
            </div>
          </div>
        );

      case "teacher":
        return (
          <div className="panel card">
            <div className="panel-header">
              <h2>ğŸ‘©â€ğŸ« Teacher Panel</h2>
              <div className="header-actions">
                <input
                  className="search"
                  placeholder="Search teachers..."
                  value={teacherQuery}
                  onChange={(e) => setTeacherQuery(e.target.value)}
                />
                <div className="count-badge">{filteredTeachers.length}</div>
              </div>
            </div>

            <form onSubmit={addTeacher} className="form grid">
              <div className="row">
                <input
                  type="text"
                  placeholder="Name"
                  value={teacherName}
                  onChange={(e) => setTeacherName(e.target.value)}
                  className="input"
                />
                <input
                  type="number"
                  placeholder="Age"
                  value={teacherAge}
                  onChange={(e) => setTeacherAge(e.target.value)}
                  className="input"
                />
              </div>
              <div className="row">
                <input
                  type="text"
                  placeholder="Qualification"
                  value={teacherQualification}
                  onChange={(e) => setTeacherQualification(e.target.value)}
                  className="input"
                />
                <input
                  type="text"
                  placeholder="CNIC (e.g. 12345-6789012-3)"
                  value={teacherCnic}
                  onChange={(e) => setTeacherCnic(e.target.value)}
                  className="input"
                />
              </div>
              <div className="row">
                <input
                  type="text"
                  placeholder="Contact No"
                  value={teacherContact}
                  onChange={(e) => setTeacherContact(e.target.value)}
                  className="input"
                />
                <input
                  type="text"
                  placeholder="Home Address"
                  value={teacherAddress}
                  onChange={(e) => setTeacherAddress(e.target.value)}
                  className="input"
                />
              </div>
              <div className="button-container">
                <button type="submit" className="add-button">
                  â• Add Teacher
                </button>
              </div>
            </form>

            <ul className="list">
              {filteredTeachers.map((t) => (
                <li key={t.id} className="list-item">
                  <div className="item-left">
                    <Avatar name={t.name} />
                    <div>
                      <strong className="item-name">{t.name}</strong>
                      <div className="muted">{t.qualification} â€¢ {t.age} yrs</div>
                      <div className="muted small">CNIC: {t.cnic}</div>
                    </div>
                  </div>
                  <div className="item-right">
                    <div className="muted small">{t.contact}</div>
                    <div className="muted small">{t.address}</div>
                    <button onClick={() => deleteTeacher(t.id)} className="delete-button" title="Delete">
                      âŒ
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        );

      case "student":
        return (
          <div className="panel card">
            <div className="panel-header">
              <h2>ğŸ‘¨â€ğŸ“ Student Panel</h2>
              <div className="header-actions">
                <input
                  className="search"
                  placeholder="Search students..."
                  value={studentQuery}
                  onChange={(e) => setStudentQuery(e.target.value)}
                />
                <div className="count-badge">{filteredStudents.length}</div>
              </div>
            </div>

            <form onSubmit={addStudent} className="form grid">
              <div className="row">
                <input
                  type="text"
                  placeholder="Name"
                  value={studentName}
                  onChange={(e) => setStudentName(e.target.value)}
                  className="input"
                />
                <input
                  type="text"
                  placeholder="Father Name"
                  value={studentFatherName}
                  onChange={(e) => setStudentFatherName(e.target.value)}
                  className="input"
                />
              </div>
              <div className="row">
                <input
                  type="text"
                  placeholder="CNIC (e.g. 12345-6789012-3)"
                  value={studentCnic}
                  onChange={(e) => setStudentCnic(e.target.value)}
                  className="input"
                />
                <input
                  type="number"
                  placeholder="Age"
                  value={studentAge}
                  onChange={(e) => setStudentAge(e.target.value)}
                  className="input"
                />
              </div>
              <div className="row">
                <input
                  type="date"
                  value={studentDob}
                  onChange={(e) => setStudentDob(e.target.value)}
                  className="input"
                />
                <select
                  value={studentGender}
                  onChange={(e) => setStudentGender(e.target.value)}
                  className="input"
                >
                  <option value="">Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                </select>
              </div>
              <div className="row">
                <input
                  type="text"
                  placeholder="Contact No"
                  value={studentContact}
                  onChange={(e) => setStudentContact(e.target.value)}
                  className="input"
                />
                <input
                  type="text"
                  placeholder="Home Address"
                  value={studentAddress}
                  onChange={(e) => setStudentAddress(e.target.value)}
                  className="input"
                />
              </div>

              <div className="button-container">
                <button type="submit" className="add-button">
                  â• Add Student
                </button>
              </div>
            </form>

            <ul className="list">
              {filteredStudents.map((s) => (
                <li key={s.id} className="list-item">
                  <div className="item-left">
                    <Avatar name={s.name} />
                    <div>
                      <strong className="item-name">{s.name}</strong>
                      <div className="muted">Roll No: {s.rollNo} â€¢ {s.gender}</div>
                      <div className="muted small">Father: {s.fatherName} â€¢ DOB: {s.dob}</div>
                    </div>
                  </div>
                  <div className="item-right">
                    <div className="muted small">CNIC: {s.cnic}</div>
                    <div className="muted small">{s.contact}</div>
                    <div className="muted small">{s.address}</div>
                    <button onClick={() => deleteStudent(s.id)} className="delete-button" title="Delete">
                      âŒ
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="container">
      <aside className="sidebar card">
        <div className="sidebar-top">
          <h3 className="logo">Admin Panel</h3>
          <div className="muted small">Manage teachers & students</div>
        </div>

        <nav className="nav">
          <button
            className={`nav-button ${activePanel === "home" ? "active" : ""}`}
            onClick={() => setActivePanel("home")}
          >
            ğŸ  Home
          </button>
          <button
            className={`nav-button ${activePanel === "teacher" ? "active" : ""}`}
            onClick={() => setActivePanel("teacher")}
          >
            ğŸ‘©â€ğŸ« Teachers <span className="badge">{teachers.length}</span>
          </button>
          <button
            className={`nav-button ${activePanel === "student" ? "active" : ""}`}
            onClick={() => setActivePanel("student")}
          >
            ğŸ‘¨â€ğŸ“ Students <span className="badge">{students.length}</span>
          </button>
        </nav>

        <div className="sidebar-bottom">
          <button onClick={handleLogout} className="logout-button">
            ğŸšª Logout
          </button>
          <div className="muted small copy">Local data Â· Persisted</div>
        </div>
      </aside>

      <main className="main-content">{renderPanel()}</main>
    </div>
  );
}
