import React, { useState } from "react";

export default function CoursesPanel({ courses, teachers, addCourse, deleteCourse }) {
  const [courseName, setCourseName] = useState("");
  const [courseCode, setCourseCode] = useState("");
  const [assignedTeacherId, setAssignedTeacherId] = useState("");
  const [assignedClass, setAssignedClass] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!courseName || !courseCode || !assignedTeacherId || !assignedClass) {
      return alert("Please provide course name, code, assigned teacher and class.");
    }
    addCourse({
      name: courseName,
      code: courseCode,
      assignedTeacherId,
      assignedClass,
    });

    setCourseName("");
    setCourseCode("");
    setAssignedTeacherId("");
    setAssignedClass("");
  };

  return (
    <div className="panel">
      <h2>üìö Courses & Curriculum</h2>

      <form onSubmit={handleSubmit} className="form teacher-form">
        <div className="field">
          <label htmlFor="courseName">Course Name</label>
          <input
            id="courseName"
            type="text"
            placeholder="Course Name"
            value={courseName}
            onChange={(e) => setCourseName(e.target.value)}
            className="input"
          />
        </div>

        <div className="field">
          <label htmlFor="courseCode">Course Code</label>
          <input
            id="courseCode"
            type="text"
            placeholder="Course Code (e.g., BSSE-101)"
            value={courseCode}
            onChange={(e) => setCourseCode(e.target.value)}
            className="input"
          />
        </div>

        <div className="field">
          <label htmlFor="assignedTeacher">Assign Teacher</label>
          <select
            id="assignedTeacher"
            value={assignedTeacherId}
            onChange={(e) => setAssignedTeacherId(e.target.value)}
            className="input"
          >
            <option value="">Select Teacher</option>
            {teachers.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
        </div>

        <div className="field">
          <label htmlFor="assignedClass">Class</label>
          <input
            id="assignedClass"
            type="text"
            placeholder="Class (e.g., BSSE-1)"
            value={assignedClass}
            onChange={(e) => setAssignedClass(e.target.value)}
            className="input"
          />
        </div>

        <div className="button-container">
          <button type="submit" className="add-button">
            ‚ûï Add Course
          </button>
        </div>
      </form>

      <ul className="list">
        {courses.map((c) => (
          <li key={c.id} className="list-item">
            <div>
              <strong>{c.name}</strong> ({c.code}) <br />
              Teacher: {c.assignedTeacherName} <br />
              Class: {c.assignedClass}
            </div>
            <button className="delete-button" onClick={() => deleteCourse(c.id)}>
              ‚ùå
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
