import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./TeacherDashboard.css";
import ExaminationPanel from "./ExaminationPanel";

export default function TeacherDashboard() {
  const navigate = useNavigate();
  const [activePanel, setActivePanel] = useState("home");

  const [students, setStudents] = useState([
    { id: 1, name: "Ali Khan", rollNo: "su72-bssem-033", attendance: 0, totalDays: 0 },
    { id: 2, name: "Sara Ahmed", rollNo: "su72-bssem-034", attendance: 0, totalDays: 0 },
    { id: 3, name: "Bilal Hussain", rollNo: "su72-bssem-035", attendance: 0, totalDays: 0 },
  ]);

  const handleLogout = () => navigate("/");

  // ‚úÖ Toggle attendance & update counts
  const toggleAttendance = (id) => {
    setStudents((prev) =>
      prev.map((s) => {
        if (s.id === id) {
          const newTotal = s.totalDays + 1;
          const newPresent = s.attendance + 1;
          const wasPresent = s.lastMarked === "Present";

          return {
            ...s,
            attendance: wasPresent ? s.attendance : newPresent,
            totalDays: newTotal,
            lastMarked: wasPresent ? "Absent" : "Present",
          };
        }
        return s;
      })
    );
  };

  // ‚úÖ Calculate individual % and overall average
  const getAttendancePercentage = (s) =>
    s.totalDays === 0 ? 0 : ((s.attendance / s.totalDays) * 100).toFixed(1);

  const calculateOverallPercentage = () => {
    const total = students.reduce((sum, s) => sum + parseFloat(getAttendancePercentage(s)), 0);
    return (total / students.length).toFixed(1);
  };

  const handleSave = () => {
    alert("‚úÖ Attendance data saved!");
    console.log(students);
  };

  // --- Panels ---
  const renderPanel = () => {
    switch (activePanel) {
      case "home":
        return (
          <div className="teacher-home">
            <h2 className="welcome-text">Welcome Teacher üë©‚Äçüè´</h2>
            <p className="sub-text">Use the sidebar to mark attendance or manage examination records.</p>
          </div>
        );

      case "attendance":
        return (
          <div className="teacher-panel">
            <h2>‚úÖ Mark Attendance</h2>
            <table className="teacher-table">
              <thead>
                <tr>
                  <th>Roll No</th>
                  <th>Name</th>
                  <th>Attendance</th>
                  <th>Progress</th>
                </tr>
              </thead>
              <tbody>
                {students.map((s) => (
                  <tr key={s.id}>
                    <td>{s.rollNo}</td>
                    <td>{s.name}</td>
                    <td>
                      <button
                        className={`toggle-btn ${
                          s.lastMarked === "Present"
                            ? "present-btn"
                            : s.lastMarked === "Absent"
                            ? "absent-btn"
                            : ""
                        }`}
                        onClick={() => toggleAttendance(s.id)}
                      >
                        {s.lastMarked ? s.lastMarked : "Mark Attendance"}
                      </button>
                    </td>
                    <td>
                      <div className="progress-container">
                        <div
                          className="progress-bar"
                          style={{ width: `${getAttendancePercentage(s)}%` }}
                        ></div>
                      </div>
                      <span className="progress-text">{getAttendancePercentage(s)}%</span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>

            <div className="summary">
              <p>
                üìä Overall Attendance Percentage:{" "}
                <strong>{calculateOverallPercentage()}%</strong>
              </p>
            </div>

            <button className="save-button" onClick={handleSave}>
              üíæ Save Attendance
            </button>
          </div>
        );

      case "examination":
        return (
          <div className="teacher-panel">
            <ExaminationPanel />
          </div>
        );

      case "profile":
        return (
          <div className="teacher-panel">
            <h2>üë©‚Äçüè´ Teacher Profile</h2>
            <div className="profile-card">
              <p><strong>Name:</strong> Ayesha Malik</p>
              <p><strong>Subject:</strong> Mathematics</p>
              <p><strong>Email:</strong> ayesha.malik@school.edu.pk</p>
              <p><strong>Employee ID:</strong> TCH-2025-09</p>
            </div>
          </div>
        );

      case "timetable":
        return (
          <div className="teacher-panel">
            <h2>üïí Weekly Timetable</h2>
            <table className="timetable-table">
              <thead>
                <tr>
                  <th>Day</th>
                  <th>08:00 - 10:00</th>
                  <th>10:15 - 12:00</th>
                  <th>01:00 - 02:30</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Monday</td>
                  <td>Class 6 (Math)</td>
                  <td>Class 7 (Math)</td>
                  <td>Staff Meeting</td>
                </tr>
                <tr>
                  <td>Tuesday</td>
                  <td>Class 8 (Algebra)</td>
                  <td>Class 9 (Geometry)</td>
                  <td>Paper Checking</td>
                </tr>
                <tr>
                  <td>Wednesday</td>
                  <td>Class 10 (Math)</td>
                  <td>Free Period</td>
                  <td>Class 7 (Math)</td>
                </tr>
                <tr>
                  <td>Thursday</td>
                  <td>Class 9 (Algebra)</td>
                  <td>Class 10 (Trigonometry)</td>
                  <td>Exam Duty</td>
                </tr>
                <tr>
                  <td>Friday</td>
                  <td>Class 8 (Math)</td>
                  <td>Class 6 (Math)</td>
                  <td>Parent Meeting</td>
                </tr>
              </tbody>
            </table>
          </div>
        );

      case "announcements":
        return (
          <div className="teacher-panel">
            <h2>üì¢ Announcements</h2>
            <ul className="announcement-list">
              <li>üóìÔ∏è Midterm exams will begin on 15th November.</li>
              <li>üìò Submit your mark sheets by 20th November.</li>
              <li>üè´ Teacher meeting scheduled on Friday 2 PM.</li>
              <li>üí° New digital attendance system launching next week.</li>
            </ul>
          </div>
        );

      case "settings":
        return (
          <div className="teacher-panel">
            <h2>‚öôÔ∏è Settings</h2>
            <p>Update your preferences or reset password.</p>
            <form className="settings-form">
              <label>
                New Password:
                <input type="password" placeholder="Enter new password" />
              </label>
              <label>
                Theme:
                <select>
                  <option>Light</option>
                  <option>Dark</option>
                </select>
              </label>
              <button type="button" className="save-button">
                üíæ Save Changes
              </button>
            </form>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="teacher-dashboard">
      {/* Sidebar */}
      <div className="teacher-sidebar">
        <h3 className="teacher-logo">Teacher Panel</h3>

        <button
          className={`nav-btn ${activePanel === "home" ? "active" : ""}`}
          onClick={() => setActivePanel("home")}
        >
          üè† Home
        </button>

        <button
          className={`nav-btn ${activePanel === "attendance" ? "active" : ""}`}
          onClick={() => setActivePanel("attendance")}
        >
          ‚úÖ Attendance
        </button>

        <button
          className={`nav-btn ${activePanel === "examination" ? "active" : ""}`}
          onClick={() => setActivePanel("examination")}
        >
          üßæ Examination
        </button>

        

        <button
          className={`nav-btn ${activePanel === "timetable" ? "active" : ""}`}
          onClick={() => setActivePanel("timetable")}
        >
          üïí Timetable
        </button>

        <button
          className={`nav-btn ${activePanel === "announcements" ? "active" : ""}`}
          onClick={() => setActivePanel("announcements")}
        >
          üì¢ Announcements
        </button>

        <button
          className={`nav-btn ${activePanel === "settings" ? "active" : ""}`}
          onClick={() => setActivePanel("settings")}
        >
          ‚öôÔ∏è Settings
        </button>

        <button className="logout-btn" onClick={handleLogout}>
          üö™ Logout
        </button>
      </div>

      {/* Main Panel Area */}
      <div className="teacher-main">{renderPanel()}</div>
    </div>
  );
}
